import React from 'react';
import { useEffect } from 'react';
import { useState } from 'react';
import axios from 'axios'
import { Link } from 'react-router-dom';

const Books = () => {
    const [books,setBook] = useState([])

    useEffect(()=>{
        const fetchAllBooks = async ()=>{
            try{
                const res = await axios.get("http://localhost:8800/books")
                setBook(res.data);
            }catch(err){
                console.log(err);
            }
        }
        fetchAllBooks()
    },[])
    return (
        <div>
            <h1>Tienda de libros</h1>
            <div className='books' >
                {books.map(book=>(
                    <div className="book" key={book.id} >
                        {book.cover && <img src={book.cover} alt="" />}
                        <h2>{book.title}</h2>
                        <p>{book.descr}</p>
                        <span>{book.price}</span>
                    </div>
                ))}
                <button><Link to="/add">Add book</Link></button>
            </div>
        </div>
    )
}


// Función para obtener el precio del producto en Amazon
async function obtenerPrecioProductoAmazon(url) {
  try {
    // Realizar solicitud a la página del producto
    const response = await axios.get(url);
    const html = response.data;

    // Expresión regular para buscar el precio del producto
    const priceRegex = /<span id="priceblock_[^"]*?price".*?>(.*?)<\/span>/;

    // Buscar el precio en el HTML utilizando la expresión regular
    const match = html.match(priceRegex);

    if (match && match[1]) {
      const price = match[1].trim();
      return price;
    } else {
      throw new Error('No se pudo encontrar el precio del producto en la página.');
    }
  } catch (error) {
    throw new Error('Error al obtener información del producto: ' + error.message);
  }
}

// URL del producto de Amazon
const productUrl = 'https://www.amazon.com/dp/B01N5TV6IJ';

// Obtener el precio del producto y mostrarlo en la consola
obtenerPrecioProductoAmazon(productUrl)
  .then(price => {
    console.log('Precio del producto:', price);
  })
  .catch(error => {
    console.error('Error:', error.message);
  });



export default Books
