import logo from './logo.svg';
import './App.css';
import {BrowserRouter, Routes, Route } from "react-router-dom";
import Books from './pages/Books';
import Add from "./pages/Add";
import Update from './pages/Update';


function App() {
  return (
    <div className="App">
      <BrowserRouter>
      <Routes>
        <Route path="/" element={<Books/>} />
        <Route path="/add" element={<Add/>} />
        <Route path="/update" element={<Update/>} />
      </Routes>
      </BrowserRouter>
    </div>
  );
}


const axios = require('axios');

// URL del producto de Amazon a verificar
const productUrl = 'https://www.amazon.com/dp/B01N5TV6IJ';

// Obtener información del producto desde la página de Amazon
axios.get(productUrl)
  .then(response => {
    const html = response.data;

    // Buscar y extraer el precio del producto
    const priceRegex = /<span id="priceblock_(?:our)?price".*?\$(.*?)<\/span>/;
    const match = html.match(priceRegex);

    if (match && match[1]) {
      const price = parseFloat(match[1].replace(',', ''));

      // Verificar el precio y establecer el umbral para detectar errores
      const errorThreshold = 50; // Establecer el umbral según tus necesidades

      if (price < errorThreshold) {
        console.log('¡Posible error de precio detectado!');
        console.log('Precio actual:', price);
        console.log('¡Verifica y confirma antes de comprar!');
      } else {
        console.log('El precio parece normal.');
      }
    } else {
      console.log('No se pudo encontrar el precio en la página.');
    }
  })
  .catch(error => {
    console.log('Error al obtener información del producto:', error);
  });


export default App;
