import logo from './logo.svg';
import './App.css';
import {BrowserRouter, Routes, Route } from "react-router-dom";
import Books from './pages/Books';
import Add from "./pages/Add";
import Update from './pages/Update';


function App() {
  return (
    <div className="App">
      <BrowserRouter>
      <Routes>
        <Route path="/" element={<Books/>} />
        <Route path="/add" element={<Add/>} />
        <Route path="/update" element={<Update/>} />
      </Routes>
      </BrowserRouter>
    </div>
  );
}


const axios = require('axios');
const express = require('express');
const cors = require('cors');

const app = express();

// Habilitar CORS en el servidor intermedio
app.use(cors());

// Ruta para realizar la solicitud al servidor de Amazon
app.get('/amazon-product', (req, res) => {
  const productUrl = 'https://www.amazon.com/dp/B01N5TV6IJ';

  axios.get(productUrl)
    .then(response => {
      // Devolver la respuesta del servidor de Amazon al cliente
      res.send(response.data);
    })
    .catch(error => {
      res.status(500).send('Error al obtener informaci칩n del producto');
    });
});

// Puerto en el que se ejecutar치 el servidor intermedio
const port = 3000;
app.listen(port, () => {
  console.log(`Servidor intermedio en funcionamiento en el puerto ${port}`);
});



export default App;
